mapper.insert:
 INSERT INTO MEMBER(      userid 
					       , upw 
					       , uName
					       , phone
					       , email
					       , grade
					       , zipcode
					       , addr1
					       , addr2
					       , birth 	)
               VALUES(    #{userid} 
              		   , #{upw}
              		   , #{uName}
              		   , #{phone}
              		   , #{email}
              		   , #{grade}
              		   , #{zipcode}
              		   , #{addr1}
              		   , #{addr2}
              		   , #{birth}
              		   )

mapper.select:
SELECT userid, upw
  FROM MEMBER
 WHERE userid = #{userid}
   AND upw = #{upw}
