mapper.insert:
 INSERT INTO MEMBER(      userid 
					       , upw 
					       , uname
					       , phone
					       , email
					       , grade
					       , zipcode
					       , addr1
					       , addr2
					       , birth 	)
               VALUES(    #{userid} 
              		   , #{upw}
              		   , #{uname}
              		   , #{phone}
              		   , #{email}
              		   , #{grade}
              		   , #{zipcode}
              		   , #{addr1}
              		   , #{addr2}
              		   , #{birth}
              		   )

mapper.select:
SELECT userid, upw, uname, phone, zipcode, addr1, addr2, birth ,grade
  FROM MEMBER
 WHERE userid = #{userid}
   AND upw = #{upw}

SELECT COUNT(*) FROM MEMBER
WHERE userid = #{userid}
  AND upw = #{upw}

mapper.delete:
DELETE FROM MEMBER
WHERE userid = #{userid}
  AND upw = #{upw}

mapper.select:
SELECT userid, upw
FROM MEMBER
WHERE email = #{email}
  AND phone = #{phone}

		SELECT userid 		
		FROM member 
        WHERE email = #{email} AND phone = #{phone}
