mapper.insert:
   		INSERT INTO sangpoom( sno 
   							,sname 
   							,img 
   							,scontent 
   							,startprice
   							,thumbimg
   							,opendate

   							)
		VALUES( (select nvl(max(sno),0)+1 from sangpoom) 
				, #{sname}
				, #{img}
				, #{scontent}
				,#{startprice}
				,#{thumbimg}
				, sysdate
				)

mapper.update:
 	UPDATE  sangpoom
	SET		sname=#{sname}
			,scontent=#{scontent}
	WHERE	sno =#{sno}
